var temperature = msg.payload.split('t=')[1];
// temperature = '23437\n' 
temperature = '23437\n'; 
return temperature.substring(0,2);

var temperature = msg;
msg = {};

var date = new Date();
var day = date.getDate();

msg.mapa = {
    "temperature_sensor": temperature + '°C',
    "current_day": day
}
return msg;

var temperature_open = msg.payload.main.temp;
var weatherIcon = msg.payload.weather[0].icon;
var code = weatherIcon.substring(0,2);
var icon = ':mostly_sunny:'; 

// Code 02 as default
switch (code) {
    case '01':
        icon = ':sunny:';
        break;
    case '03':
        icon = ':barely_sunny:';
        break;
    case '04':
        icon = ':cloud:';
        break;
    case '09':
        icon = ':rain_cloud:';
        break;
    case '10':
        icon = ':partly_sunny_rain:';
        break;
    case '11':
        icon = ':thunder_cloud_and_rain:';
        break;
}

temperature_open = (temperature_open + '').substring(0,2);

var message = "Zeppelin - Temperatura";
message += "\n Europa: " + msg.mapa.temperature_sensor;
message += "\n Recife: " + temperature_open + '°C ' + icon; 
message += "\n\n Atualizado a cada 5 minutos."

msg.payload = {
 "text" : message
}

return msg;
